            int len;
            byte[] buffer = new byte[1024];
            while ((len = in.read(buffer)) &gt; 0) {
                out.write(buffer, 0, len);
                out.flush();
            }
        } catch (Exception e) {
            log.info(&quot;Zip from txt occur error,Detail message:{}&quot;, e.toString());
        } finally {
            try {
                if (in != null) in.close();
                if (out != null) {
                    out.closeEntry();
                    out.close();
                }
