class BasicLinkedList&lt;T&gt; implements Iterable&lt;T&gt; {
    public BasicLinkedList() {
        head = null;
        tail = null;
        listSize = 0;
    }

    public BasicLinkedList&lt;T&gt; addToEnd(T data) {
        Node newNode = new Node(data);
        if (head == null) {
            head = newNode;
            tail = newNode;
        } else {
            tail.next = newNode;
            tail = newNode;
        }
        listSize++;
        return this;
    }

    public BasicLinkedList&lt;T&gt; addToFront(T data) {
        Node newNode = new Node(data);
        newNode.next = head;
        head = newNode;
        listSize++;
        return this;
    }

    public T getFirst() {
        if (head == null) {
            return null;
        }
        return head.data;
    }

    public T getLast() {
        if (tail == null) {
            return null;
        }
        return tail.data;
    }

    public BasicLinkedList&lt;T&gt; remove(T targetData, java.util.Comparator&lt;T&gt; comparator) {
        Node previousNode = null;
        Node currentNode = head;
        while (currentNode != null) {
            if (comparator.compare(currentNode.data, targetData) == 0) {
                //this is for the first entry specifically
                if (currentNode == head) {
                    head = head.next;
                }
                //this is for the last entry
                else if (currentNode == tail) {
                    tail = previousNode;
                    previousNode.next = null;
                } else {
                    previousNode.next = currentNode.next;
                }
                listSize--;
                break;
            }
            previousNode = currentNode;
            currentNode = currentNode.next;
        }
        return this;
    }

    public T retrieveFirstElement() {
        if (head == null) {
            return null;
        }

        Node firstElement = head;

        head = head.next;
        listSize--;
        return firstElement.data;
    }

    public T retrieveLastElement() {
        Node currentNode = head;
        T data = null;
        if (tail == null) {
            return null;
        } else {
                while (currentNode != null &amp;&amp; currentNode.next != tail) {
                    currentNode = currentNode.next;
                }
                data = tail.data;
                tail = currentNode;
                tail.next = null;
        }

        listSize--;
        return data;
    }

    public int getSize() {
        return listSize;
    }

    /* Node definition */
    protected class Node {
        protected T data;
        protected Node next;

        protected Node(T data) {
            this.data = data;
            next = null;
        }
    }

    /* We have both head and tail */
    protected Node head, tail;

    /* size */
    protected int listSize;

    @Override
    public Iterator&lt;T&gt; iterator() {

        return new IteratorForLinkedList();

    }

    private class IteratorForLinkedList implements Iterator&lt;T&gt; {
        Node first = head;

        @Override
        public boolean hasNext() {
            return first != null;
        }

        @Override
        public T next() {
            Node oldFirst = first;
            T item = oldFirst.data;
            first = first.next;
            return item;
        }

    }
}
