            for (int i=0; i&lt;list.length; i++) cost[i+1] = list[i]; // setup costs
            for (int i=0; i&lt;to.length; i++) dag[to[i]] = from[i]; // setup DAG
            path = new int[N]; // the current path
            top = 0; // top of path
