    Date d =  new SimpleDateFormat(&quot;yyyy-MM-dd&quot;).parse(&quot;2014-10-12&quot;);
    Query query = session.createQuery(&quot;select count(*) from Visit v where trunc(v.date)=:date and v.visitor.id=:visitorId&quot;);
