            listener.onStopped();
        }
        running = false;
        shouldRun = false;
    }

    public final boolean isRunning() {
        return running;
    }
    public final boolean safeStop() {
            if (listener != null) {
                listener.onStopped();
            }
            shouldRun = false;
            running = false;
            return true;
    }

    // Listener defined earlier
    public interface RunnableDemoListener {
        public void onDataError(String title);
        public void onDataSuccess(Response&lt;ResponseBody&gt; responseBody);
        public void onStopped();
    }

