    public static Boolean valueOf(String s) {
    	return toBoolean(s) ? TRUE : FALSE;
    }

    public static boolean parseBoolean(String s) {
        return toBoolean(s);
    }

    public Boolean(String s) {
	    this(toBoolean(s));
    }

    private static boolean toBoolean(String name) {
