        ).forEach(item-&gt;{
              list.add(item.substring(21,item.lastIndexOf(&quot;,&quot;))+&quot;-&quot;+item.substring(item.lastIndexOf(&quot;ue=&quot;)+3,item.lastIndexOf(&quot;)&quot;)));
