fun Iterable&lt;String&gt;.commonPrefix(ignoreCase: Boolean = false): String {
    var commonPrefix: String? = null
    for (string in this) {
        if (commonPrefix == null) {
            commonPrefix = string
            continue
        }
        commonPrefix = commonPrefix.commonPrefixWith(string, ignoreCase)
    }
    return commonPrefix ?: error(&quot;empty iterable&quot;)
}

fun String.commonPrefixWith(otherString: String, ignoreCase: Boolean = false): String {
    var offset = 0
    while (true) {
        val thisChar = this.getOrNull(offset) ?: break
        val otherChar = otherString.getOrNull(offset) ?: break
        if (!thisChar.equals(otherChar, ignoreCase)) {
            break
        }
        offset++
    }
    return this.substring(0, offset)
}
```

