        if (CollectionUtils.isEmpty(interceptors)) {
                interceptors = new ArrayList&lt;&gt;();
        }
        interceptors.add(new MultipartMixedBoundaryRewritingInterceptor());
        restTemplate.setInterceptors(interceptors);
