    public class GetObjectData {
    
        public static void main(String[] args) {
    
         final String usage = &quot;\n&quot; +
                    &quot;Usage:\n&quot; +
                    &quot;    &lt;bucketName&gt; &lt;keyName&gt; &lt;path&gt;\n\n&quot; +
                    &quot;Where:\n&quot; +
                    &quot;    bucketName - The Amazon S3 bucket name. \n\n&quot;+
