    public RawModel loadToVAO(float[] vertexPositions) {
        int vaoID = createVAO();
        storeDataInAttrList(vertexPositions, 0);
        unbindVAO();
        return new RawModel(vaoID, vertexPositions.length / 3);
    }

    public void cleanup() {
