    private int createVAO() {
        int vaoID = GL30.glGenVertexArrays();
        GL30.glBindVertexArray(vaoID);
        vaoList.add(vaoID);
        return vaoID;
    }

    private void unbindVAO(){
        GL30.glBindVertexArray(0);
    }

    private int createVBO() {
        int vboID = GL15.glGenBuffers();
        GL15.glBindBuffer(GL15.GL_ARRAY_BUFFER, vboID);
        vboList.add(vboID);
        return vboID;
    }

    private void storeDataInAttrList(float[] data, int attrPos) {
        int vboID = createVBO();
        FloatBuffer buffer = convertArrayToFloatBuffer(data);
        GL15.glBufferData(GL15.GL_ARRAY_BUFFER, buffer, GL15.GL_STATIC_DRAW);
        GL20.glVertexAttribPointer(attrPos, 3, GL11.GL_FLOAT, false, 0, 0);
        // Unbinding VBO.
        GL15.glBindBuffer(GL15.GL_ARRAY_BUFFER, 0);
    }



    private FloatBuffer convertArrayToFloatBuffer(float[] data) {
        FloatBuffer buffer = BufferUtils.createFloatBuffer(data.length);
        buffer.put(data);
        buffer.flip();
        return buffer;
    }

    public static ModelLoader getInstance() {
        return instance;
    }
}

