    Set.of( &quot;1 3 3 4 5 42&quot;.split( &quot; &quot; ) );
    isDistinct = Boolean.TRUE;
}
catch ( IllegalArgumentException e )
{
    isDistinct = Boolean.FALSE;
}

System.out.println( &quot;Input is distinct: &quot; + Objects.requireNonNull( isDistinct ) );
