import java.io.*;

public class WriteToFile {
    public static void main(String[] args) {
        try (FileWriter writer = new FileWriter(&quot;/tmp/file.txt&quot;, true)) {
            for (String arg : args) {
                writer.write(arg + &quot;\n&quot;);
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
    }
}
