        @Override
        public void onReceive(Context context, Intent intent){goToUpdateActivity();}
    };

    @Override
    protected void onDestroy() {
        try{
        unregisterReceiver(RECEIVER_UPDATE);
        }catch (Exception ignored){}
        unregisterReceiver(RECEIVER_CLOSE);
        super.onDestroy();
    }

    @Override
    protected void onPause() {
        unregisterReceiver(RECEIVER_UPDATE);
        super.onPause();
    }

    @Override
    protected void onResume(){
        super.onResume();
        registerReceiver(RECEIVER_UPDATE, new IntentFilter(&quot;update&quot;));
    }

