    enum State {
      Off, On
    }

    enum Uninitialized implements Data {
      Uninitialized
    }

    public class SimpleFSM extends AbstractFSM&lt;State, Data&gt; {
        {
            // fsm body
            startWith(Off, Uninitialized);
            
            // transitions
