        for(String record:input) {
            String split[] = record.split(&quot;:&quot;);
            if (split.length == 0 || split.length &gt; 2) continue;

            String born = split[0];

            // Born
            if(populationDelta.get(born) != null) {
                populationDelta.put(born, 0);
            }
            else {
                populationDelta.put(born, populationDelta.get(born).intValue() + 1);
            }

            if (split.length != 2) continue;
            String died = split[1];

            // Died
            if(populationDelta.get(died) != null) {
