```java
import java.io.ByteArrayOutputStream;
import java.io.IOException;
import java.io.InputStream;
import java.nio.charset.Charset;
import java.nio.charset.StandardCharsets;
import java.util.Arrays;
import java.util.Objects;


public class ChunkedInputStream extends InputStream {
    public static final byte[] EMPTY = new byte[0];
    private final Charset charset;
    private InputStream in;
    private int chunkSize;
    private int pos;
    private boolean bof = true;
    private boolean eof = false;
    private boolean closed = false;

    public ChunkedInputStream(final InputStream in, Charset charset) throws IOException {

        if (in == null) {
            throw new IllegalArgumentException(&quot;InputStream parameter may not be null&quot;);
        }
        this.in = in;
        this.pos = 0;

        this.charset = Objects.requireNonNullElse(charset, StandardCharsets.US_ASCII);
    }


    public int read() throws IOException {

        if (closed) {
            throw new IOException(&quot;Attempted read from closed stream.&quot;);
        }
        if (eof) {
            return -1;
        }
        if (pos &gt;= chunkSize) {
            nextChunk();
            if (eof) {
                return -1;
            }
        }
        pos++;
        return in.read();
    }

    public int read(byte[] b, int off, int len) throws IOException {

        if (closed) {
            throw new IOException(&quot;Attempted read from closed stream.&quot;);
        }

        if (eof) {
            return -1;
        }
        if (pos &gt;= chunkSize) {
            nextChunk();
            if (eof) {
                return -1;
            }
        }
        len = Math.min(len, chunkSize - pos);
        int count = in.read(b, off, len);
        pos += count;
        return count;
    }

    public int read(byte[] b) throws IOException {
        return read(b, 0, b.length);
    }

    public byte[] readChunk() throws IOException {
        if (eof) {
            return EMPTY;
        }
        if (pos &gt;= chunkSize) {
            nextChunk();
            if (eof) {
                return EMPTY;
            }
        }
        byte[] chunk = new byte[chunkSize];
        int nbByteRead = 0;
        int remainingByteToChunk = chunkSize;
        while (remainingByteToChunk &gt; 0 &amp;&amp; !eof) {
            nbByteRead = read(chunk, chunkSize - remainingByteToChunk, remainingByteToChunk);
            remainingByteToChunk -= nbByteRead;
        }

        if (remainingByteToChunk == 0) {
            return chunk;
        } else {
            return Arrays.copyOf(chunk, chunk.length - remainingByteToChunk);
        }
    }

    private void readCRLF() throws IOException {
        int cr = in.read();
        int lf = in.read();
        if ((cr != &#39;\r&#39;) || (lf != &#39;\n&#39;)) {
            throw new IOException(
                    &quot;CRLF expected at end of chunk: &quot; + cr + &quot;/&quot; + lf);
        }
    }

    private void nextChunk() throws IOException {
        if (!bof) {
            readCRLF();
        }
        chunkSize = getChunkSizeFromInputStream(in);
        bof = false;
        pos = 0;
        if (chunkSize == 0) {
            eof = true;
        }
    }

    private int getChunkSizeFromInputStream(final InputStream in)
            throws IOException {

        ByteArrayOutputStream baos = new ByteArrayOutputStream();
        // States: 0=normal, 1=\r was scanned, 2=inside quoted string, -1=end
        int state = 0;
        while (state != -1) {
            int b = in.read();
            if (b == -1) {
                throw new IOException(&quot;chunked stream ended unexpectedly&quot;);
            }
            switch (state) {
                case 0:
                    switch (b) {
                        case &#39;\r&#39;:
                            state = 1;
                            break;
                        case &#39;\&quot;&#39;:
                            state = 2;
                            /* fall through */
                        default:
                            baos.write(b);
                    }
                    break;

                case 1:
                    if (b == &#39;\n&#39;) {
                        state = -1;
                    } else {
                        // this was not CRLF
                        throw new IOException(&quot;Protocol violation: Unexpected&quot;
                                + &quot; single newline character in chunk size&quot;);
                    }
                    break;

                case 2:
                    switch (b) {
                        case &#39;\\&#39;:
                            b = in.read();
                            baos.write(b);
                            break;
                        case &#39;\&quot;&#39;:
                            state = 0;
                            /* fall through */
                        default:
                            baos.write(b);
                    }
                    break;
                default:
                    throw new RuntimeException(&quot;assertion failed&quot;);
            }
        }

        //parse data
        String dataString = baos.toString(charset);
        int separator = dataString.indexOf(&#39;;&#39;);
        dataString = (separator &gt; 0)
                ? dataString.substring(0, separator).trim()
                : dataString.trim();

        int result;
        try {
            result = Integer.parseInt(dataString.trim(), 16);
        } catch (NumberFormatException e) {
            throw new IOException(&quot;Bad chunk size: &quot; + dataString);
        }
        return result;
    }

    public void close() throws IOException {
        if (!closed) {
            try {
                if (!eof) {
                    exhaustInputStream(this);
                }
            } finally {
                eof = true;
                closed = true;
            }
        }
    }

    static void exhaustInputStream(InputStream inStream) throws IOException {
        // read and discard the remainder of the message
        byte[] buffer = new byte[1024];
        while (inStream.read(buffer) &gt;= 0) {
            ;
        }
