            String value = token.substring(positionOfEqualsSign + 1);
            finalLine.append(value);
        } else {
            finalLine.append(token);
        }
        if (tokenizer.hasMoreTokens()) {
            finalLine.append(&quot;,&quot;);
        }
    }
    System.out.println(&quot;   final: &quot; + finalLine);
    System.out.println();
}
