    String s1= &quot;BBBABAABBBBABB&quot;; // output 5          
    long count = Pattern.compile(&quot;(?&lt;=(A.{0,100}))B(?=(.*A))&quot;).matcher(s1).results().count();
    if(count &gt; 0){
