          while (iteratorPDFReader.hasNext()) {
            PdfReader pdfReader = iteratorPDFReader.next();
    
            // Create a new page in the target for each source page.
            while (pageOfCurrentReaderPDF &lt; pdfReader.getNumberOfPages()) {
              document.newPage();
