    BigDecimal bd1 = new BigDecimal(inputstring.charAt(0)==&#39;1&#39;?1:0);
    BigDecimal two = new BigDecimal(2);
    for (int i = 1; i&lt;inputstring.length(); i++) {
        bd1 = bd1.multiply(two);
