    static class CustomSpringOpaqueTokenIntrospector extends SpringOpaqueTokenIntrospector {

        public CustomSpringOpaqueTokenIntrospector(OAuth2ResourceServerProperties properties) {
            super(properties.getOpaquetoken().getIntrospectionUri(), properties.getOpaquetoken().getClientId(), properties.getOpaquetoken().getClientSecret());
            setRequestEntityConverter(defaultRequestEntityConverter(URI.create(properties.getOpaquetoken().getIntrospectionUri())));
        }

        private Converter&lt;String, RequestEntity&lt;?&gt;&gt; defaultRequestEntityConverter(URI introspectionUri) {
            return (token) -&gt; {
                //here you can modify the body of the request
                HttpHeaders headers = requestHeaders();
                MultiValueMap&lt;String, String&gt; body = requestBody(token);
                return new RequestEntity&lt;&gt;(body, headers, HttpMethod.POST, introspectionUri);
            };
        }

        private HttpHeaders requestHeaders() {
            HttpHeaders headers = new HttpHeaders();
            headers.setAccept(Collections.singletonList(MediaType.APPLICATION_JSON));
            return headers;
