	// The VM calls JNI_OnLoad when the native library is loaded (for example, through System.loadLibrary).
	jint JNI_OnLoad(JavaVM* vm, void* reserved) {
        JNIEnv* env;
		if ((*vm)-&gt;GetEnv(vm, (void **) &amp;env, JNI_VERSION_1_6) != JNI_OK) {
