    public class BidirectionalMap&lt;K, V&gt; extends HashMap&lt;K, V&gt; {
	private static final long serialVersionUID = 1L;
	public HashMap&lt;V, K&gt; inversedMap = new HashMap&lt;V, K&gt;();

