                }
    
                var leftCheck = CheckHeight(node.LeftChild, ref height);
                if (!leftCheck) return false;
                height--;
                var rightCheck = CheckHeight(node.RightChild, ref height);
                if (!rightCheck) return false;
                height--;
                return true;
            }
    
    
