    public int maxBlock(String str) {
      int maxBlock = 0;
      int currentBlock = 1;
      
      if (str.length() &lt; 1) {
        return maxBlock;
      } else {
        maxBlock = 1;
      }
      
      for (int i = 0; i &lt; str.length() - 1; i++) {
        if (str.charAt(i) ==  str.charAt(i+1)) {
          currentBlock++;
          if (currentBlock &gt; maxBlock) {
            maxBlock = currentBlock;
          }
        } else {
          currentBlock = 1;
        }
      }
      
      return maxBlock;
    }

