    regexPattern = &quot;^(?=.{1,64}@)[A-Za-z0-9\\+_-]+(\\.[A-Za-z0-9\\+_-]+)*@&quot; 
        + &quot;[^-][A-Za-z0-9\\+-]+(\\.[A-Za-z0-9\\+-]+)*(\\.[A-Za-z]{2,})$&quot;;
    assertTrue(EmailValidation.patternMatches(emailAddress, regexPattern));
