        private String name;
    }
    
    class Patient {
        private int id;
        private String name;
    }
    
    class Illness {
        private int id;
        private String name;
    }
    
    class Appointment {
        private Doctor doctor;
        private Patient patient;
        private Illness illness;
        private Date date;
    
        public Appointment(
