    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {                                         
        // Calculate all the grade
        String englishScore, mathScore,scienceScore,geoScore,bmScore;
        double average;
        double gpa = 0;
        
        englishScore = englishTF.getText();
        mathScore = mathTF.getText();
        scienceScore = scienceTF.getText();
        geoScore = geoTF.getText();
        bmScore = bmTF.getText();
        
        double english = Double.parseDouble(englishScore);
        double math = Double.parseDouble(mathScore);
        double science = Double.parseDouble(scienceScore); 
        double geo = Double.parseDouble(geoScore);
        double bm = Double.parseDouble(bmScore);
        
        average = ((english + math + science + geo + bm) / 5);
        
        if (english &gt;= 90){
           EletterTF.setText(&quot;A&quot;);
           gpa = gpa + 4.0;
        } else if (english &gt;= 80 &amp;&amp; english &lt; 90){
