    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {                                         
        String english = englishTF.getText();
        String math = mathTF.getText();
        String science = scienceTF.getText();
        String geo = geoTF.getText();
        String bm = bmTF.getText(); 
        String average = averageTF.getText();
        String gpa = gpaTF.getText();
        try
        {
         FileWriter Writer = new FileWriter(&quot;grade.txt&quot;,true); 
         Writer.write (&quot;&quot;+english+&quot; &quot;+math+&quot; &quot;+science+&quot; &quot;+geo+&quot; &quot;+bm+&quot; &quot;+average+&quot; &quot;+gpa+&quot; &quot;);
         Writer.write(System.getProperty(&quot;line.seperator&quot;));
         Writer.close();
         JOptionPane.showMessageDialog(null,&quot;Success !&quot;);
         setVisible(false);
         new InsertData().setVisible(true);
        }
        catch (Exception E)
        {
           JOptionPane.showMessageDialog(null,&quot;Error :( &quot;);  
        }
        
    }                                        

