                listener,
                currentYear,
                currentMonth,
                currentDay);

        // Set dates
        datePickerDialog.getDatePicker().setMinDate(minTime);
        datePickerDialog.getDatePicker().setMaxDate(maxTime);

        // Show dialog
        datePickerDialog.show();
    }
}

