                    while (r &gt;= 0 &amp;&amp; r &lt;= 18 &amp;&amp; c &gt;= 0 &amp;&amp; c &lt;= 18 &amp;&amp; map[r][c] == player)
                    {
                        count ++;
                        r += 0;
                        c += 1;
                    }
                    if (count == 5)
                    {
                        return true;
                    } else {
                        count = 0;
                    }
                }
            }
        }
        return false;
    }
