        this.ID.set(ID);    
        this.type.set(type);
        this.credits.set(credits);        
        this.scholarship.set(scholarship);
        if (deductionCode.equals(&quot;2022AUT&quot;))
            this.deduction.bind(this.totalFee.multiply(this.deductionRate));
        else 
            this.deduction.set(0.00);
    }
    
