	} catch (Exception ignored) {

	} finally {
		if (byteArrayOutputStream != null) {
			try {
				byteArrayOutputStream.close ();
			} catch (IOException ignore) {
			}
		}
	}
	return (byteArrayOutputStream != null ? byteArrayOutputStream.toByteArray () : null);
}
