                .collect(Collectors.toList());
        driver.navigate().refresh();
        List&lt;String&gt; lastImages = driver.findElements(By.tagName(&quot;img&quot;))
                .stream()
                .map(e -&gt; e.getAttribute(&quot;src&quot;))
                .collect(Collectors.toList());
        Assertions.assertTrue(Collections.disjoint(firstImages, lastImages));
    }

[Collections.disjoint][1] Returns `true` if the two specified collections have no elements in common.


  [1]: https://docs.oracle.com/en/java/javase/17/docs/api/java.base/java/util/Collections.html#disjoint(java.util.Collection,java.util.Collection)

