            Integer weekdayPos2 = sortOrder.get(i2.getWeekday());
            if (weekdayPos2 == null)
            {
                throw new IllegalArgumentException(&quot;Bad weekday encountered: &quot; +
                   i2.getWeekday());
            }
            return weekdayPos1.compareTo(weekdayPos2);
