    public void sortAbasedOnB(String[] listA, double[] listB) {
        for (int i = 0; i &lt; listB.length - 1; i++) {
            for (int j = listB.length - 1; j &gt; i; j--) {
                if (listB[j] &lt; listB[j - 1]){
                    double tempD = listB[j - 1];
                    listB[j - 1] = listB[j];
                    listB[j] = tempD;
                    String tempS = listA[j - 1];
                    listA[j - 1] = listA[j];
                    listA[j] = tempS;
                }
            }
        }
    }

