            {
                max = array[right];
                pos = 1;
            }
            else
            {
                max = array[left];
                pos = 0;
            }
            
            if(((Comparable &lt;E&gt;) array[parent]).compareTo(max) &lt; 0)
            {
                if (pos == 0)
                {
                    swap(parent, left);
                    return;
                }
                else
                {
                    swap(parent, right);
                    return;
                }
            }
            return;
        }
        if (left &gt;= lastposition || right &gt;= lastposition)
