       return org.getUnitId() != null &amp;&amp; org.getUnitType() != null &amp;&amp; org.getContact() != null &amp;&amp; org.getUnitId().equals(this.getUnitId()) &amp;&amp; org.getUnitType().equals(this.getUnitType) &amp;&amp; org.getContact().equals(this.getContact());
    }

