                &quot;)&quot;;
    
    
        SQLiteDatabase mDB;
    
    
        public DBHelper(Context context) {
            super(context, DBNAME, null, DBVERSION);
            mDB = this.getWritableDatabase();
        }
    
        @Override
        public void onCreate(SQLiteDatabase db) {
            db.execSQL(crtTest1SQL);
