        public Cursor getCSVRows(String table) {
            Cursor rv;
            String[] columns;
            switch (table) {
                case TB_TEST1:
                    columns = new String[]{COL_TEST1_ID + &quot;||&#39;,&#39;||&quot; + COL_TEST1_NAME + &quot;||&#39;,&#39;||&quot; + COL_TEST1_EMAIL};
                    break;
                    default:
                        return null;
            }
            return mDB.query(table,columns,null,null,null,null,null);
