     */
    private Integer getIndexOfArrayNode(Node node) {
        if(isArrayNode(node)) {
            int leftCount = 0;

            Node currentNode = node.getPreviousSibling();

            while(currentNode != null) {
                leftCount++;
                currentNode = currentNode.getPreviousSibling();
            }
            return leftCount;
        } else {
