        public class ColorBand {

            private float[] fractions;
            private Color[] colors;

            public ColorBand(float[] fractions, Color[] colors) {
                this.fractions = fractions;
                this.colors = colors;
            }

            public Color colorAt(float progress) {
                Color color = null;
                if (fractions != null) {
                    if (colors != null) {
                        if (fractions.length == colors.length) {
                            int[] indicies = getFractionIndicies(progress);

                            float[] range = new float[]{fractions[indicies[0]], fractions[indicies[1]]};
                            Color[] colorRange = new Color[]{colors[indicies[0]], colors[indicies[1]]};

                            float max = range[1] - range[0];
                            float value = progress - range[0];
                            float weight = value / max;

                            color = blend(colorRange[0], colorRange[1], 1f - weight);
                        } else {
                            throw new IllegalArgumentException(&quot;Fractions and colours must have equal number of elements&quot;);
                        }
                    } else {
                        throw new IllegalArgumentException(&quot;Colours can&#39;t be null&quot;);
                    }
                } else {
                    throw new IllegalArgumentException(&quot;Fractions can&#39;t be null&quot;);
                }
                return color;
            }

            protected int[] getFractionIndicies(float progress) {
                int[] range = new int[2];

                int startPoint = 0;
                while (startPoint &lt; fractions.length &amp;&amp; fractions[startPoint] &lt;= progress) {
                    startPoint++;
                }

                if (startPoint &gt;= fractions.length) {
                    startPoint = fractions.length - 1;
                }

                range[0] = startPoint - 1;
                range[1] = startPoint;

                return range;
            }

            protected Color blend(Color color1, Color color2, double ratio) {
                float r = (float) ratio;
                float ir = (float) 1.0 - r;

                float rgb1[] = new float[3];
                float rgb2[] = new float[3];

                color1.getColorComponents(rgb1);
                color2.getColorComponents(rgb2);

                float red = rgb1[0] * r + rgb2[0] * ir;
                float green = rgb1[1] * r + rgb2[1] * ir;
                float blue = rgb1[2] * r + rgb2[2] * ir;

                if (red &lt; 0) {
                    red = 0;
                } else if (red &gt; 255) {
                    red = 255;
                }
                if (green &lt; 0) {
                    green = 0;
                } else if (green &gt; 255) {
                    green = 255;
                }
                if (blue &lt; 0) {
                    blue = 0;
                } else if (blue &gt; 255) {
                    blue = 255;
                }

                Color color = null;
                try {
                    color = new Color(red, green, blue);
                } catch (IllegalArgumentException exp) {
                    NumberFormat nf = NumberFormat.getNumberInstance();
                    System.out.println(nf.format(red) + &quot;; &quot; + nf.format(green) + &quot;; &quot; + nf.format(blue));
                    exp.printStackTrace();
                }
                return color;
            }
        }
    }


