    public static ArrayList&lt;String&gt; toStringArrayList(JSONArray jsonArray) {

      ArrayList&lt;String&gt; stringArray = new ArrayList&lt;String&gt;();
      int arrayIndex;

      for (
        arrayIndex = 0;
        arrayIndex &lt; jsonArray.length();
        arrayIndex++) {

