        String input = &quot;=CF=D4=C4=C5=CC=D8=CE=D9=CD =D4=D2=C1=CE=DB=C5=CD =D2=C5=DA=C0=CD=.eml&quot;;
        String decode = EncodingUtils.decodeKoi8r(input);
