     public static &lt;T&gt; BinaryOperator&lt;T&gt; maxBy(Comparator&lt;? super T&gt; comparator) {
            Objects.requireNonNull(comparator);
            return (a, b) -&gt; comparator.compare(a, b) &gt;= 0 ? a : b;
        }

