package de.simpleclient.gui.font2.simplified;

import java.awt.Color;

import com.mojang.blaze3d.systems.RenderSystem;
import com.mojang.blaze3d.vertex.PoseStack;
import de.simpleclient.gui.font2.Wrapper;

