        final static int ARRAY_LENGTH = 10_000_000;
    
        @State(Scope.Benchmark)
        public static class TestBuddy {
    
            int[] as;
            int xor;
    
            @Setup(Level.Trial)
            public void init(){
                as = new int[ARRAY_LENGTH];
                xor = 0;
