        conditions.put(s -&gt; false, &quot;3&quot;);
    }
    
    public static Stream&lt;Map.Entry&lt;Predicate&lt;String&gt;, String&gt;&gt; getConditions() {
        return conditions.entrySet().stream();
    }  
}
